mapper.select:
select id as id,code as code,name as name,
       press as press,author as author,pdate as pdate,
       uprice as uprice,major as major,grade as grade
from tb_teaching_material
where delflag='0'

        and  code = #{code,jdbcType=VARCHAR}


        and  name = #{name,jdbcType=VARCHAR}


        and  major = #{major,jdbcType=VARCHAR}


        and  grade = #{grade,jdbcType=VARCHAR}

mapper.update:
update  tb_teaching_material
set delflag ='1'
where id = #{books.id,jdbcType=VARCHAR}
